#include "RC_PQ.h"
void rc_pq(const int w, uint64_t & p, uint64_t & q){
//  e - 2       = 0....
//  phi - 1     = 0....
    switch (w){
        case 16:
            p = 0xb7e1U;
            q = 0x9e37U;
            break;
        case 32:
            p = 0xb7e15163UL;
            q = 0x9e3779b9UL;
            break;
        case 64:
            p = 0xb7e151628aed2a6bULL;
            q = 0x9e3779b97f4a7c15ULL;
            break;
        default:
            throw std::runtime_error("Error: No values defined for w = " + std::to_string(w));
            break;
    }
}

